# Process to extract and transform each light entry
def transform_input(input_str):
    # Split the input string into individual light entries
    entries = input_str.split("\n")
    transformed_entries = []

    for entry in entries:
        # Extract the JSON part of each entry
        json_str = entry.split(": ", 1)[1]
        # Convert string to dictionary
        device_dict = eval(json_str)
        # Append to the list of transformed entries
        transformed_entries.append(device_dict)

    return transformed_entries

